import React, { useState } from 'react';
import './Address.css';

function App() {
  const [data, setData] = useState([]);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newContact, setNewContact] = useState('');
  const [newLocation, setNewLocation] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);

  const addTask = () => {
    if (newName && isValidEmail(newEmail) && isValidContact(newContact) && newLocation) {
      const newData = [...data, { name: newName, email: newEmail, contact: newContact, location: newLocation }];
      setData(newData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
    }
  };

  const deleteTask = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  const editTask = (index) => {
    setEditingIndex(index);
    const taskToEdit = data[index];
    setNewName(taskToEdit.name);
    setNewEmail(taskToEdit.email);
    setNewContact(taskToEdit.contact);
    setNewLocation(taskToEdit.location);
  };

  const updateTask = () => {
    if (newName && isValidEmail(newEmail) && isValidContact(newContact) && newLocation && editingIndex !== null) {
      const updatedData = [...data];
      updatedData[editingIndex] = { name: newName, email: newEmail, contact: newContact, location: newLocation };
      setData(updatedData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
      setEditingIndex(null);
    }
  };

  // Function to validate the email
  const isValidEmail = (email) => {
    const emailPattern = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
    return emailPattern.test(email);
  };

  // Function to validate the contact
  const isValidContact = (contact) => {
    const contactPattern = /^\d+$/;
    return contactPattern.test(contact);
  };

  return (
    <div>
      <h1>Task List</h1>
      <div className="table-container">
        {/* ... rest of the code remains the same ... */}
      </div>

      <div className="add-task-container">
        <h2>Add a Task</h2>
        <div className="input-container">
          <input
            type="text"
            placeholder="Name"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Email"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="Contact"
            value={newContact}
            onChange={(e) => setNewContact(e.target.value)}
          />
          <input
            type="text"
            placeholder="Location"
            value={newLocation}
            onChange={(e) => setNewLocation(e.target.value)}
          />
          <button className='add-button' onClick={addTask}>Add</button>
        </div>
      </div>
    </div>
  );
}

export default App;
