import React, { useState } from 'react';
import './LoginSignup.css';
import user_icon from '../assets/user_icon.png';
import email_icon from '../assets/email_icon.png';
import lock_icon from '../assets/lock_icon.png';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");

  const toggleAction = () => {
    setAction(action === "Sign Up" ? "Login" : "Sign Up");
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission here based on the action (Sign Up or Login)
    if (action === "Sign Up") {
      // Handle Sign Up
      console.log("Sign Up");
    } else {
      // Handle Login
      console.log("Login");
    }
  };

  return (
    <div className='container'>
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <form onSubmit={handleFormSubmit}>
        {action === "Sign Up" && (
          <div className="input">
            <img src={user_icon} alt="" />
            <input type="text" placeholder='name' />
          </div>
        )}
        <div className="input">
          <img src={email_icon} alt="" />
          <input type="email" placeholder='email' />
        </div>
        <div className="input">
          <img src={lock_icon} alt="" />
          <input type="password" placeholder='password' />
        </div>
        {action === "Login" && (
          <div className="forgot-password">
            Lost password?<span>click here! </span>
          </div>
        )}
        <div className="submit-container">
          <input type="submit" className="submit" value="Sign Up" />
          <input type="submit" className="submit" value="Login" />
        </div>
      </form>
      <div className="toggle-action" onClick={toggleAction}>
        {action === "Sign Up" ? "Already have an account? Login" : "Don't have an account? Sign Up"}
      </div>
    </div>
  );
}

export default LoginSignup;
