import React, { useState } from 'react';
import './VerifyEmail.css';
import ResetPassword from './ResetPassword';

const VerifyEmail = () => {
  // State to manage the email input value
  const [email, setEmail] = useState('');

  // Event handler for the submit button
  const handleSubmission = () => {
    // Use the email value, for example, you can log it
    console.log('Email submitted:', email);

    // You can also perform other actions with the email value here
  };

  return (
    <div>
      <h1 className="head1">Address Management System</h1>
      <label className="label1">Please Enter Your Email</label>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)} // Update the email state
        className="email1"
      />
      <br /><br />
      <button type="submit" onClick={handleSubmission}>
        Submit
      </button>
    </div>
  );
};

export default VerifyEmail;
