import React, { useState, useEffect } from 'react';
import './Address.css';

function App() {
  const [data, setData] = useState([]);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newContact, setNewContact] = useState('');
  const [newLocation, setNewLocation] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // Display 10 addresses per page

  useEffect(() => {
    const savedData = JSON.parse(localStorage.getItem('addressData')) || [];
    setData(savedData);
  }, []);

  useEffect(() => {
    localStorage.setItem('addressData', JSON.stringify(data));
  }, [data]);

  const addTask = () => {
    if (newName && newEmail && newContact && newLocation) {
      const newData = [...data, { name: newName, email: newEmail, contact: newContact, location: newLocation }];
      setData(newData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
    }
  };

  const deleteTask = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  const editTask = (index) => {
    setEditingIndex(index);
    const taskToEdit = data[index];
    setNewName(taskToEdit.name);
    setNewEmail(taskToEdit.email);
    setNewContact(taskToEdit.contact);
    setNewLocation(taskToEdit.location);
  };

  const updateTask = () => {
    if (newName && newEmail && newContact && newLocation && editingIndex !== null) {
      const updatedData = [...data];
      updatedData[editingIndex] = { name: newName, email: newEmail, contact: newContact, location: newLocation };
      setData(updatedData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
      setEditingIndex(null);
    }
  };

  const nextPage = () => {
    if (currentPage < Math.ceil(data.length / itemsPerPage)) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentData = data.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="app-container">
      <div className="address-list-container">
        <h1 className='addressHead'>Address List</h1>
        <div className="add-task-container">
          <h2>Add New Address</h2>
          <div className="input-container">
            <input
              type="text"
              placeholder="Name"
              className='name text-box-shadow'
              style={{ textAlign: 'center' }}
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
            />
            <input
              type="text"
              placeholder="Email"
              className='email text-box-shadow'
              style={{ textAlign: 'center' }}
              value={newEmail}
              onChange={(e) => setNewEmail(e.target.value)}
            />
            <input
              type="text"
              placeholder="Contact"
              className='contact text-box-shadow'
              style={{ textAlign: 'center' }}
              value={newContact}
              onChange={(e) => setNewContact(e.target.value)}
            />
            <input
              type="text"
              placeholder="Location"
              className='location text-box-shadow'
              style={{ textAlign: 'center' }}
              value={newLocation}
              onChange={(e) => setNewLocation(e.target.value)}
            />
            <button className="add-button" onClick={addTask}>Add</button>
          </div>
        </div>
      </div>

      <div className="table-container">
        <div className="table-scroll">
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Contact</th>
                <th>Location</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {currentData.map((task, index) => (
                <tr key={index}>
                  <td>
                    {index === editingIndex ? (
                      <input
                        type="text"
                        value={newName}
                        onChange={(e) => setNewName(e.target.value)}
                      />
                    ) : task.name}
                  </td>
                  <td>
                    {index === editingIndex ? (
                      <input
                        type="text"
                        value={newEmail}
                        onChange={(e) => setNewEmail(e.target.value)}
                      />
                    ) : task.email}
                  </td>
                  <td>
                    {index === editingIndex ? (
                      <input
                        type="text"
                        value={newContact}
                        onChange={(e) => setNewContact(e.target.value)}
                      />
                    ) : task.contact}
                  </td>
                  <td>
                    {index === editingIndex ? (
                      <input
                        type="text"
                        value={newLocation}
                        onChange={(e) => setNewLocation(e.target.value)}
                      />
                    ) : task.location}
                  </td>
                  <td>
                    {index === editingIndex ? (
                      <button onClick={updateTask} className="update-button">Update</button>
                    ) : (
                      <>
                        <button onClick={() => editTask(index)} className="edit-button">Edit</button>
                        <button onClick={() => deleteTask(index)} className="delete-button">Delete</button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="pagination-controls">
        <button onClick={prevPage} disabled={currentPage === 1}>Previous</button>
        <button onClick={nextPage} disabled={currentPage >= Math.ceil(data.length / itemsPerPage)}>Next</button>
      </div>
    </div>
  );
}

export default App;
