//import React from 'react'

import React, { useState } from 'react';

function App() {
  const [data, setData] = useState([]);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newContact, setNewContact] = useState('');
  const [newLocation, setNewlocation] = useState('');

  const addTask = () => {
  if (newName && newEmail && newContact && newLocation) {
    const newData = [...data, { name: newName, email: newEmail, contact: newContact, location: newLocation }];
    setData(newData);
    setNewName('');
    setNewEmail('');
    setNewContact('');
    setNewLocation('');
  }
};


  const deleteTask = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  return (
    <div>
      <h1>Task List</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Contact</th>
            <th>location</th>

            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((task, index) => (
            <tr key={index}>
              <td>{task.name}</td>
              <td>{task.email}</td>
              <td>{task.contact}</td>
              <td>
                <button onClick={() => deleteTask(index)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div>
        <h2>Add a Task</h2>
        <input
          type="text"
          placeholder="Name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Email"
          value={newEmail}
          onChange={(e) => setNewEmail(e.target.value)}
        />
        <input
          type="text"
          placeholder="Contact"
          value={newContact}
          onChange={(e) => setNewContact(e.target.value)}
        />
        <input
          type="text"
          placeholder="location"
          value={newLocation}
          onChange={(e) => setNewlocation(e.target.value)}
        />
        <button onClick={addTask}>Add</button>
      </div>
    </div>
  );
}

export default App;
