import React, { useState, useEffect } from 'react';
import './Address.css';

function App() {
  const [data, setData] = useState([]);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newContact, setNewContact] = useState('');
  const [newLocation, setNewLocation] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);

  // Load data from localStorage when the component mounts
  useEffect(() => {
    const savedData = JSON.parse(localStorage.getItem('addressData')) || [];
    setData(savedData);
  }, []);

  // Save data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('addressData', JSON.stringify(data));
  }, [data]);

  const addTask = () => {
    if (newName && newEmail && newContact && newLocation) {
      const newData = [...data, { name: newName, email: newEmail, contact: newContact, location: newLocation }];
      setData(newData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
    }
  };

  const deleteTask = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  const editTask = (index) => {
    setEditingIndex(index);
    const taskToEdit = data[index];
    setNewName(taskToEdit.name);
    setNewEmail(taskToEdit.email);
    setNewContact(taskToEdit.contact);
    setNewLocation(taskToEdit.location);
  };

  const updateTask = () => {
    if (newName && newEmail && newContact && newLocation && editingIndex !== null) {
      const updatedData = [...data];
      updatedData[editingIndex] = { name: newName, email: newEmail, contact: newContact, location: newLocation };
      setData(updatedData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
      setEditingIndex(null);
    }
  };

  return (
    <div>
      <h1>Address List</h1>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Contact</th>
              <th>Location</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {data.map((task, index) => (
              <tr key={index}>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                  />
                ) : task.name}</td>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newEmail}
                    onChange={(e) => setNewEmail(e.target.value)}
                  />
                ) : task.email}</td>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newContact}
                    onChange={(e) => setNewContact(e.target.value)}
                  />
                ) : task.contact}</td>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newLocation}
                    onChange={(e) => setNewLocation(e.target.value)}
                  />
                ) : task.location}</td>
                <td>
                  {index === editingIndex ? (
                    <button onClick={updateTask} className="update-button">Update</button>
                  ) : (
                    <>
                      <button onClick={() => editTask(index)} className="edit-button">Edit</button>
                      <button onClick={() => deleteTask(index)} className="delete-button">Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="add-task-container">
        <h2>Add New Address</h2>
        <div className="input-container">
          <input
            type="text"
            placeholder="Name" className='name'
            style={{ textAlign: 'center' }}
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Email" className='email'
            
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="Contact" className='contact'
            value={newContact}
            onChange={(e) => setNewContact(e.target.value)}
          />
          <input
            type="text"
            placeholder="Location" className='location'
            value={newLocation}
            onChange={(e) => setNewLocation(e.target.value)}
          />
          <button className="add-button" onClick={addTask}>Add</button>
        </div>
      </div>
    </div>
  );
}

export default App;
