import React, { useState, useEffect } from 'react';
import './Address.css';

function App() {
  const [data, setData] = useState([]);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newContact, setNewContact] = useState('');
  const [newLocation, setNewLocation] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');
  const [showAddForm, setShowAddForm] = useState(false); // New state for showing/hiding the add form
  const itemsPerPage = 5;

  useEffect(() => {
    const savedData = JSON.parse(localStorage.getItem('addressData')) || [];
    setData(savedData);
  }, []);

  useEffect(() => {
    localStorage.setItem('addressData', JSON.stringify(data));
  }, [data]);

  const addTask = () => {
    if (newName && newEmail && newContact && newLocation) {
      const newData = [...data, { name: newName, email: newEmail, contact: newContact, location: newLocation }];
      setData(newData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
      setShowAddForm(false); // Hide the form after adding
    }
  };

  const deleteTask = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  const editTask = (index) => {
    setEditingIndex(index);
    const taskToEdit = data[index];
    setNewName(taskToEdit.name);
    setNewEmail(taskToEdit.email);
    setNewContact(taskToEdit.contact);
    setNewLocation(taskToEdit.location);
  };

  const updateTask = () => {
    if (newName && newEmail && newContact && newLocation && editingIndex !== null) {
      const updatedData = [...data];
      updatedData[editingIndex] = { name: newName, email: newEmail, contact: newContact, location: newLocation };
      setData(updatedData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
      setEditingIndex(null);
    }
  };

  const nextPage = () => {
    if (currentPage < Math.ceil(filteredData.length / itemsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;

  // Filter the data based on the search query
  const filteredData = data.filter((item) => {
    const search = searchQuery.toLowerCase();
    return (
      item.name.toLowerCase().includes(search) ||
      item.email.toLowerCase().includes(search) ||
      item.contact.toLowerCase().includes(search) ||
      item.location.toLowerCase().includes(search)
    );
  });

  return (
    <div className="app-container">
      <div className="address-list-container">
        <h1 className='addressHead'>Address List</h1>
        
        {/* Add New Address button */}
        <button className="add-new-button" onClick={() => setShowAddForm(!showAddForm)}>
          {showAddForm ? 'Cancel' : 'Add New Address'}
        </button>

        {showAddForm && (
          <div className="add-task-container">
            <h2>Add New Address</h2>
            <div className="input-container">
              {/* Input fields for name, email, contact, and location */}
              <input
                type="text"
                placeholder="Name"
                className='name text-box-shadow'
                style={{ textAlign: 'center' }}
                value={newName}
                onChange={(e) => setNewName(e.target.value)}
              />
              <input
                type="text"
                placeholder="Email"
                className='email text-box-shadow'
                style={{ textAlign: 'center' }}
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
              />
              <input
                type="text"
                placeholder="Contact"
                className='contact text-box-shadow'
                style={{ textAlign: 'center' }}
                value={newContact}
                onChange={(e) => setNewContact(e.target.value)}
              />
              <input
                type="text"
                placeholder="Location"
                className='location text-box-shadow'
                style={{ textAlign: 'center' }}
                value={newLocation}
                onChange={(e) => setNewLocation(e.target.value)}
              />
              <button className="add-button" onClick={addTask}>Add</button>
            </div>
          </div>
        )}
        
        {/* Rest of your code for search and table */}
      </div>
    </div>
  );
}

export default App;
