import React, { useState } from 'react';
import './Ad.css'; // Create a separate CSS file for styling (e.g., App.css)

function App() {
  const [data, setData] = useState([]);
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newContact, setNewContact] = useState('');
  const [newLocation, setNewLocation] = useState('');

  const addTask = () => {
    if (newName && newEmail && newContact && newLocation) {
      const newData = [...data, { name: newName, email: newEmail, contact: newContact, location: newLocation }];
      setData(newData);
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
    }
  };

  const deleteTask = (index) => {
    const newData = [...data];
    newData.splice(index, 1);
    setData(newData);
  };

  return (
    <div>
      <h1>Task List</h1>
      <div className="grid-container">
        {data.map((task, index) => (
          <div key={index} className="grid-item">
            <div>
              <strong>Name:</strong> {task.name}
            </div>
            <div>
              <strong>Email:</strong> {task.email}
            </div>
            <div>
              <strong>Contact:</strong> {task.contact}
            </div>
            <div>
              <strong>Location:</strong> {task.location}
            </div>
            <div>
              <button onClick={() => deleteTask(index)}>Delete</button>
            </div>
          </div>
        ))}
      </div>

      <div>
        <h2>Add a Task</h2>
        <input
          type="text"
          placeholder="Name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Email"
          value={newEmail}
          onChange={(e) => setNewEmail(e.target.value)}
        />
        <input
          type="text"
          placeholder="Contact"
          value={newContact}
          onChange={(e) => setNewContact(e.target.value)}
        />
        <input
          type="text"
          placeholder="Location"
          value={newLocation}
          onChange={(e) => setNewLocation(e.target.value)}
        />
        <button onClick={addTask}>Add</button>
      </div>
    </div>
  );
}

export default App;
