import React, { useState } from 'react';
import { useAddress } from './AddressContext';

function Address() {
  const { state, dispatch } = useAddress();
  const { data } = state;
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [newContact, setNewContact] = useState('');
  const [newLocation, setNewLocation] = useState('');
  const [editingIndex, setEditingIndex] = useState(null);

  const addAddress = () => {
    if (newName && newEmail && newContact && newLocation) {
      dispatch({
        type: 'ADD_ADDRESS',
        payload: { name: newName, email: newEmail, contact: newContact, location: newLocation },
      });
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
    }
  }

  const editAddress = (index) => {
    setEditingIndex(index);
    const taskToEdit = data[index];
    setNewName(taskToEdit.name);
    setNewEmail(taskToEdit.email);
    setNewContact(taskToEdit.contact);
    setNewLocation(taskToEdit.location);
  }

  const updateAddress = () => {
    if (newName && newEmail && newContact && newLocation && editingIndex !== null) {
      dispatch({
        type: 'UPDATE_ADDRESS',
        payload: { data: { name: newName, email: newEmail, contact: newContact, location: newLocation }, index: editingIndex },
      });
      setNewName('');
      setNewEmail('');
      setNewContact('');
      setNewLocation('');
      setEditingIndex(null);
    }
  }

  const deleteAddress = (index) => {
    dispatch({
      type: 'DELETE_ADDRESS',
      payload: index,
    });
  }

  return (
    <div>
      <h1>Address List</h1>
      <div>
        <h2>Add New Address</h2>
        <div>
          {/* Input fields for newName, newEmail, newContact, newLocation */}
          <button onClick={addAddress}>Add</button>
        </div>
      </div>
      <div>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Contact</th>
              <th>Location</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {data.map((task, index) => (
              <tr key={index}>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                  />
                ) : task.name}</td>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newEmail}
                    onChange={(e) => setNewEmail(e.target.value)}
                  />
                ) : task.email}</td>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newContact}
                    onChange={(e) => setNewContact(e.target.value)}
                  />
                ) : task.contact}</td>
                <td>{index === editingIndex ? (
                  <input
                    type="text"
                    value={newLocation}
                    onChange={(e) => setNewLocation(e.target.value)}
                  />
                ) : task.location}</td>
                <td>
                  {index === editingIndex ? (
                    <button onClick={updateAddress}>Update</button>
                  ) : (
                    <>
                      <button onClick={() => editAddress(index)}>Edit</button>
                      <button onClick={() => deleteAddress(index)}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Address;
