import React, { useState } from 'react';
import './LoginSignup.css';
import user_icon from '../assets/user_icon.png';
import email_icon from '../assets/email_icon.png';
import lock_icon from '../assets/lock_icon.png';

const SignupLogin = () => {
  const [action, setAction] = useState("Sign Up");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState(""); // Add a state for displaying messages

  const toggleAction = () => {
    setAction(action === "Sign Up" ? "Login" : "Sign Up");
    setMessage(""); // Clear any previous messages
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (action === "Sign Up") {
      if (password === confirmPassword) {
        // You can add logic here to send data to the backend for registration
        // Display success or error messages accordingly
        setMessage("Registration successful!");
      } else {
        setMessage("Passwords do not match.");
      }
    } else {
      // Add login logic here
      setMessage("Login successful!");
    }
  };

  return (
    <div className='container page-content'>
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <div className="form-container">
        <form onSubmit={handleFormSubmit}>
          {action === "Sign Up" && (
            <div className="input Box">
              <img src={user_icon} alt="" />
              <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
          )}
          <div className="input Box">
            <img src={email_icon} alt="" />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input">
            <img src={lock_icon} alt="" />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {action === "Sign Up" && (
            <div className="input">
              <img src={lock_icon} alt="" />
              <input
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </div>
          )}
          {action === "Login" && (
            <div className="forgot-password1">
              <span>Forgot your password?</span>
              <a href="ForgotPasswordLink">Click here</a>
            </div>
          )}
          <div className="submit-container">
            <button type="submit" className="submit">
              {action === "Sign Up" ? "Sign Up" : "Login"}
            </button>
          </div>
        </form>
        <div className="message">{message}</div>
      </div>
      <div className="toggle-action" onClick={toggleAction}>
        {action === "Sign Up"
          ? "Already have an account? Login"
          : "Don't have an account? Sign Up"}
      </div>
    </div>
  );
}

export default SignupLogin;
